import React from 'react';
import './HospForm.css';

class HospForm extends React.Component{
    constructor(props){
        super(props);
        this.featureOptions=['feature1','feature2','feature3','feature4'];
        this.state={
            selectedFeatures: [],
            featureMap: {'feature1': 1}
        };
    }
    render(){
        var ctr=0;
        const options=this.featureOptions.map((option)=><option key={option} value={option}>{option}</option>);
        const features=this.state.selectedFeatures.map((feature)=><select key={feature} name={"Feature"+(ctr++)}>
                {options}
            </select>);
        return <form className='HospForm'>
            {features}
        </form>
    }
}
export default HospForm;
