import React from 'react';
import './HospForm.css';

const DATA_TYPE={
    TEXT: 'text',
    BOOL: 'bool',
    LIST: 'list'
};

function create_input_field(name_attr){
    var data_type=DATA_TYPE[document.querySelector('select[name='+name_attr+']')];
    if(data_type===DATA_TYPE['TEXT']){
        return <input type='text' name={name_attr} id={id_attr}/>
    }
    else if(data_type===DATA_TYPE['BOOL']){
        return <select name={name_attr} id={id_attr}>
            <option>Yes</option>
            <option>No</option>
        </select>;
    }
    else if(data_type===DATA_TYPE['LIST']){
        var options=list.map((val)=><option key={val}>val</option>);
        return <select name={name_attr} id={id_attr}>
            {options}
        </select>;
    }
}

class HospForm extends React.Component{
    constructor(props){
        super(props);
        this.featureOptions=['feature1','feature2','feature3','feature4'];
        this.featureDataType={
            feature1: DATA_TYPE['TEXT'],
            feature2: DATA_TYPE['BOOL'],
            feature3: DATA_TYPE['TEXT'],
            feature4: DATA_TYPE['BOOL']
        }
        this.state={
            selectedFeatures: ['feature1','feature2'],
            featureMap: {'feature1': 1,'feature2':12}
        };
    }
    addFeature(event){
        event.preventDefault();
        console.log(document.querySelector('select[name=feature'+this.state.selectedFeatures.length+']').value); 
        console.log(document.querySelector('input[id=feature'+this.state.selectedFeatures.length+']').value);
        var newFeature=document.querySelector('select[name=feature'+this.state.selectedFeatures.length+']').value; 
        var newValue=document.querySelector('input[id=feature'+this.state.selectedFeatures.length+']').value;
        var tempMap=this.state.featureMap;
        var tempList=this.state.selectedFeatures;
        tempList.push(newFeature);
        tempMap[newFeature]=newValue;
        this.setState({
            featureMap: tempMap,
            selectedFeatures: tempList
        });
    }
    removeFeature(event){
        event.preventDefault();
        var feature=event.target.value;
        var tempList=[];
        var tempMap=this.state.featureMap;
        for(var i=0;i<this.state.selectedFeatures.length;i++){
            if(this.state.selectedFeatures[i]!==feature){
                tempList.push(this.state.selectedFeatures[i]);
            }
        }
        delete tempMap[feature];
        this.setState({
            featureMap: tempMap,
            selectedFeatures: tempList
        });
    }
    render(){
        var options=this.featureOptions.map((feature)=>{
            if(!this.state.selectedFeatures.includes(feature)){
                return <option key={feature}>{feature}</option>
            }
        });
        console.log(options);
        var setFeatures=this.state.selectedFeatures.map((feature)=>{
            return <div key={feature}>{feature}-->{this.state.featureMap[feature]}<button value={feature} onClick={this.removeFeature.bind(this)}>remove</button></div>;
        });
        //<input type="text" name={'feature'+this.state.selectedFeatures.length} id={'feature'+this.state.selectedFeatures.length}/>
        return <div>
            {setFeatures}
            <select name={"feature"+this.state.selectedFeatures.length}>
                {options}
            </select>
            {create_input_field('feature'+this.state.selectedFeatures.length)}
            <button onClick={this.addFeature.bind(this)}>add</button>
        </div>
    }
}
export default HospForm;
