import React from 'react';
import './DocForm.css';

class DocForm extends React.Component{
    constructor(props){
        super(props);
        this.state={
            patientDetails:{
                'pat1@gmail.com': {
                    medical_history: {
                        disease1:{
                            medications: ['med1','med2','med3'],
                            avoid: ['av1','av2','av3']
                        },
                        disease2: {
                            medications: ['med1','med2','med3'],
                            avoid: ['av1','av2','av3']
                        }
                    },
                    current_medications:{
                        disease1: {
                            medications: [],
                            avoid: []
                        },
                    },
                },
                'pat2@gmail.com': {}
            }
        };
    }
    componentDidMount(){
        fetch('http://localhost:5000/get_patient_details',{
            method: 'GET',
            credentials: 'include'
        })
        .then(e=>e.json())
        .then(function(e){
            this.setState({
                patientDetails: e
            })
        });
    }
    showPatientDetails(event){
        event.preventDefault();
        var patId=event.target.textContent;
        console.log(patId);
        console.log(this.state);
    }
    render(){
        var patientIds=[];
        /*
            data is of the form:
            data: [patid(email add): {
                    medical_history: [disease1:{
                            medications: [med1,med2,med3].
                            avoid: [av1,av2,av3]
                        },
                        disease2: {
                            medicatiosn: [med1,med2,med3],
                            avoid: [av1,av2,av3]
                        }
                    ],
                    current_medications: [disease1: {
                            medications: [],
                            avoid: []
                        },
                        disease2: {
                            medications: [],
                            avoid: []
                        },
                    ]
                },
                patid: ...,
                patid: ...
            ]
        */
        for(var key in this.state.patientDetails){
            patientIds.push(key);
        }
        var patientButtons=patientIds.map(id=><button key={id} onClick={this.showPatientDetails.bind(this)} className='PatientIdButton'>{id}</button>);
        patientButtons=<ul>{patientButtons}</ul>
        return patientButtons;
    }
}
export default DocForm;
