import React from 'react';
import FormOption from './FormOption';
import './Form.css';

var verylightblue='#e6fcfc';

class Form extends React.Component{
    constructor(props){
        super(props);
        this.state={
            loginSelected: false
        };
    }
    selectLogin(){
        this.setState({
            loginSelected: true
        });
    }
    selectSignup(){
        this.setState({
            loginSelected: false
        });
    }
    render(){
        return <form>
                <FormOption text="Login" 
                    height="5%" 
                    width="37%"
                    left="5%"
                    top="2%"
                    padding="3%"
                    backgroundColor={(this.state.loginSelected && verylightblue) || 'white'}
                    onClick={this.selectLogin.bind(this)}
                >
                </FormOption>
                <FormOption text="Sign Up" 
                    height="5%" 
                    width="37%"
                    left="50%"
                    top="2%"
                    padding="3%"
                    backgroundColor={(!this.state.loginSelected && verylightblue) || 'white'}
                    onClick={this.selectSignup.bind(this)}
                >
                </FormOption>
                <label id="lb1">Email ID <input type="text" placeholder="email@gmail.com" name="email"/></label>
                <label id="lb2">Password <input type="password" placeholder="password" name="password"/></label>
                <label id="lb3" style={{
                    display: (this.state.loginSelected && 'none') || 'block'
                }}
                >
                    Confirmation 
                    <input type="password" placeholder="confirm password" name="confirmation"/>
                </label>
                <button className="SignupButton" style={{
                    display: ((this.state.loginSelected) && 'none') || 'block'
                }}
                >
                    Sign Up
                </button>
                <button className="LoginButton" style={{
                    display: ((!this.state.loginSelected) && 'none') || 'block'
                }}
                >
                    Log In
                </button>
            </form>;
    }
}
export default Form;
