import React from 'react';

class Disease extends React.Component{
    constructor(props){
        super(props);
        this.state={
            medications: [],
            avoid: []
        }
    }
    addDisease(event){
        var disObj={}
        disObj['name']=document.querySelector('input[name=new_disease]').value;
        disObj['medications']=this.state.medication;
        disObj['avoid']=this.state.avoid;
        this.props.addDisease(disObj);
    }
    getDiseasesComponent(){
        var diseaseList=[];
        for(var key in this.props.currentDiseases){
            diseaseList.push(key);
        }
        return <ul>{diseaseList.map((dis)=><li>
                {dis}
                Medications: 
                <Container addElm={this.props.addMedication} removeElm={this.props.removeMedication} list={this.props.currentDiseases[dis]['medications']}/>
                Avoid: 
                <Container addElm={this.props.addAvoid} removeElm={this.props.removeAvoid} list={this.props.currentDiseases[dis]['avoid']}/>
                <NewDiseaseEntry/>
            </li>)}
            Disease Name: <input type='text' name='new_disease'/>
            Medications: 
            <Container addElm={this.addMedication.bind(this)} removeElm={this.removeMedication.bind(this)} list={[]}/>
            Avoid: 
            <Container addElm={this.addAvoid.bind(this)} removeElm={this.removeAvoid.bind(this)} list={[]}/>
            <button onClick={this.addDisease.bind(this)}>Add Disease</button>
        </ul>;
    }
    render(){
        return this.getDiseasesComponent();
    }
}
export default Disease;
