import React from 'react';
import './HospForm.css';

class HospForm extends React.Component{
    constructor(props){
        super(props);
        this.featureOptions=['feature1','feature2','feature3','feature4'];
        this.state={
            selectedFeatures: [],
            featureMap: {}
        };
    }
    addFeature(event){
        event.preventDefault();

    }
    render(){
        const options=this.featureOptions.map((feature)=>{
            if(this.state.selectedFeatures.includes(feature))return<option key={feature}>{feature}</option>
            else return <option></option>
        });
        const setFeatures=this.state.selectedFeatures.map((feature)=><div>{feature}-->{this.state.featureMap[feature]}</div>);
        return <div>
            {setFeatures}
            <select name={"feature"+this.state.selectedFeatures.length}>
                {options}
            </select>
            <input type='text' name={'feature'+this.state.selectedFeatures.length}/>
            <button onClick={this.addFeature.bind(this)}>add</button>
        </div>
    }
}
export default HospForm;
