import React from 'react';

class Container extends React.Component{
    constructor(props){
        super(props);
    }
    removeElm(e){
        e.preventDefault();
        console.log('remove',e.target.className);
        this.props.removeElm(this.props.diseaseName,e.target.className);
    }
    addElm(e){
        e.preventDefault();
        var newElm=document.querySelectorAll('input[name=new_elm]');
        newElm.map(e=>{console.log(e.value)});
        if(this.props.type==='medication'){
            newElm=newElm[0].value;
        }
        else if(this.props.type==='avoid'){
            newElm=newElm[1].value;
        }
        else if(this.props.type==='new_medication'){
            newElm=newElm[3].value;
        }
        else if(this.props.type==='new_avoid'){
            newElm=newElm[4].value;
        }
        console.log('Container.js: addElm(): newElm=',newElm);
        this.props.addElm(this.props.diseaseName,newElm);
    }
    render(){
        return <ul>
            {this.props.list.map(elm=><li>{elm} <button className={elm} onClick={this.removeElm.bind(this)}>remove</button></li>)}
            <input type='text' name='new_elm'/><button onClick={this.addElm.bind(this)}>Add</button>
        </ul>;
    }
}

export default Container;
