import React from 'react';
import './HospForm.css';
import axios from 'axios';

class HospForm extends React.Component{
    constructor(props){
        super(props);
        this.location=null;
        function setLocation(e){
            console.log('setLocation: ',e);
            this.location={'latitude':e.coords.latitude,'longitude':e.coords.longitude};
        }
        this.featureOptions=['feature1','feature2','feature3','feature4'];
        this.state={
            selectedFeatures: [],
            featureMap: {},
            fetchedData: false
        };
        window.navigator.geolocation.getCurrentPosition(setLocation.bind(this),console.log);
        console.log(this.location);
    }
    componentDidMount(){
        fetch('http://localhost:3000/get_hosp_data',{
            method: 'GET',
        })
    }
    addFeature(event){
        event.preventDefault();
        console.log(document.querySelector('select[name=feature'+this.state.selectedFeatures.length+']').value); 
        console.log(document.querySelector('input[name=feature'+this.state.selectedFeatures.length+']').value);
        var newFeature=document.querySelector('select[name=feature'+this.state.selectedFeatures.length+']').value; 
        var newValue=document.querySelector('input[name=feature'+this.state.selectedFeatures.length+']').value;
        var tempMap=this.state.featureMap;
        var tempList=this.state.selectedFeatures;
        tempList.push(newFeature);
        tempMap[newFeature]=newValue;
        this.setState({
            featureMap: tempMap,
            selectedFeatures: tempList
        });
    }
    sendData(event){
        event.preventDefault();
        var data=this.state.featureMap;
        data['location']=this.location;
        data['hosp_id']=document.querySelector('input[name=hospid]').value;
        data['address']=document.querySelector('input[name=address]').value;
        axios.post('http://localhost:5000/send_hospital_details/',{
            method: 'no-cors',    
            data: data
        })
        .then((response)=>console.log(response));
    }
    removeFeature(event){
        event.preventDefault();
        var feature=event.target.value;
        var tempList=[];
        var tempMap=this.state.featureMap;
        for(var i=0;i<this.state.selectedFeatures.length;i++){
            if(this.state.selectedFeatures[i]!==feature){
                tempList.push(this.state.selectedFeatures[i]);
            }
        }
        delete tempMap[feature];
        this.setState({
            featureMap: tempMap,
            selectedFeatures: tempList
        });
    }
    render(){
        var options=this.featureOptions.map((feature)=>{
            if(!this.state.selectedFeatures.includes(feature)){
                return <option key={feature}>{feature}</option>
            }
        });
        console.log(options);
        var setFeatures=this.state.selectedFeatures.map((feature)=>{
            return <div key={feature}>{feature}-->{this.state.featureMap[feature]}<button value={feature} onClick={this.removeFeature.bind(this)}>remove</button></div>;
        });
        return <div>
            Hospital ID<input type="text" name='hospid'/>
            Address<input type="text" name='address'/>
            {setFeatures}
            <select name={"feature"+this.state.selectedFeatures.length}>
                {options}
            </select>
            <input type="text" name={'feature'+this.state.selectedFeatures.length}/>
            <button onClick={this.addFeature.bind(this)}>add</button><br/>
            <button onClick={this.sendData.bind(this)}>Send Details</button>
        </div>
    }
}
export default HospForm;
