import React from 'react';
import './HospForm.css';

class HospForm extends React.Component{
    constructor(props){
        super(props);
        this.featureOptions=['feature1','feature2','feature3','feature4'];
        this.state={
            selectedFeatures: ['feature1','feature2'],
            featureMap: {'feature1': 1,'feature2':12}
        };
    }
    render(){
        var ctr=0;
        const features=this.state.selectedFeatures.map((feature)=>{
            const options=this.featureOptions.map((option)=>{
                if(!this.state.selectedFeatures.includes(feature)) return <option key={option} value={option}>{option}</option>
                else return <option key={option} value={option} selected>{option}</option>
            });
            console.log(options);
            return <select key={feature} name={"Feature"+(ctr++)}>
                {options}
            </select>
        });
        console.log(features);
        return <form className='HospForm'>
            {features}
        </form>
    }
}
export default HospForm;
