import React from 'react';
import './HospForm.css';

class HospForm extends React.Component{
    constructor(props){
        super(props);
        this.location=null;
        function setLocation(e){
            console.log('setLocation: ',e);
            this.location={'latitude':e.coords.latitude,'longitude':e.coords.longitude};
        }
        this.featureOptions=['bed','ICU','doctors','nurses'];
        this.state={
            selectedFeatures: [],
            featureMap: {},
            fetchedData: false
        };
        window.navigator.geolocation.getCurrentPosition(setLocation.bind(this),console.log);
        console.log(this.location);
    }
    componentDidMount(){
        console.log('component mounted');
        fetch('http://localhost:5000/get_hospital_details/',{
            method: 'GET',
            credentials: 'include'
        })
        .then(function(e){
            return e.json();
        })
        .then((e)=>{
            console.log('receieved: ',e)
            var selectedFeatures=[]
            var featureMap={}
            for(var key in e){
                if(this.featureOptions.includes(key)){
                    selectedFeatures.push(key)
                    featureMap[key]=e[key]                    
                }
            }
            document.querySelector('input[name=address]').value=e['address']
            this.setState({
                featureMap: featureMap,
                selectedFeatures: selectedFeatures
            });
        })
        .catch(function(e){
            console.log(e)
        });
    }
    addFeature(event){
        event.preventDefault();
        console.log(document.querySelector('select[name=new_feature]').value); 
        console.log(document.querySelector('input[name=new_feature]').value);
        var newFeature=document.querySelector('select[name=new_feature]').value; 
        var newValue=document.querySelector('input[name=new_feature]').value;
        var tempMap=this.state.featureMap;
        var tempList=this.state.selectedFeatures;
        tempList.push(newFeature);
        tempMap[newFeature]=newValue;
        this.setState({
            featureMap: tempMap,
            selectedFeatures: tempList
        });
    }
    sendData(event){
        event.preventDefault();
        var data=this.state.featureMap;
        data['location']=this.location;
        data['address']=document.querySelector('input[name=address]').value;
        fetch('http://localhost:5000/send_hospital_details/',{
            method: 'POST',
            credentials: 'include',
            body: JSON.stringify(data)
        })
        .then((response)=>console.log(response))
        .catch(e=>{console.log(e)});
    }
    removeFeature(event){
        event.preventDefault();
        console.log('removing ',event.target.value);
        var feature=event.target.value;
        var tempList=[];
        var tempMap=this.state.featureMap;
        for(var i in this.state.selectedFeatures.length){
            if(this.state.selectedFeatures[i]!==feature){
                tempList.push(this.state.selectedFeatures[i]);
            }
        }
        console.log(tempList);
        delete tempMap[feature];
        this.setState({
            featureMap: tempMap,
            selectedFeatures: tempList
        });
    }
    render(){
        var options=this.featureOptions.map((feature)=>{
            if(!this.state.selectedFeatures.includes(feature)){
                return <option key={feature}>{feature}</option>
            }
        });
        console.log(options);
        var ctr=1;
        var setFeatures=this.state.selectedFeatures.map((feature)=>{
            return <div key={feature}>{feature}-->{this.state.featureMap[feature]}<button value={feature} onClick={this.removeFeature.bind(this)}>remove</button></div>;
        });
        setFeatures.map(i=>{console.log(i.key)});
        return <div>
            Address<input type="text" name='address'/><br/>
            {setFeatures}
            <select name={'new_feature'}>
                {options}
            </select>
            <input type="text" name='new_feature'/>
            <button onClick={this.addFeature.bind(this)}>add</button><br/>
            <button onClick={this.sendData.bind(this)}>Send Details</button>
        </div>
    }
}
export default HospForm;
